mapper.select:
select

from news AS t1 JOIN (SELECT ROUND(0.9*(SELECT MAX(news_id) FROM `news`)) AS news_id) AS t2
where 1=1 and t1.news_id >= t2.news_id and t1.is_sensitive is not null

    and t1.lang_type = #{paramCondition.langType}


    and t1.is_sensitive = #{paramCondition.isSensitive}


    and sensitive_category = #{paramCondition.sensitiveCategory}


    and (news_time between CONCAT(#{paramCondition.beginTime},' 00:00:00') and CONCAT(#{paramCondition.endTime},' 23:59:59'))

ORDER BY t1.news_id DESC

SELECT
t1.websitename,COUNT(*) AS num FROM news AS t1 JOIN (SELECT ROUND(0.9*(SELECT MAX(news_id) FROM `news`)) AS news_id) AS t2
WHERE
1=1 AND t1.news_id >= t2.news_id AND t1.websitename IS NOT NULL

    and t1.lang_type = #{paramCondition.langType}


    and is_sensitive = #{paramCondition.isSensitive}


    and sensitive_category = #{paramCondition.sensitiveCategory}


    and t1.websitename like CONCAT('%',#{paramCondition.websitename},'%')


    and (news_time between CONCAT(#{paramCondition.beginTime},' 00:00:00') and CONCAT(#{paramCondition.endTime},' 23:59:59'))

GROUP BY
t1.websitename
ORDER BY num DESC
LIMIT 5;

SELECT
websitename,COUNT(*) AS num FROM news
WHERE 1=1

    and lang_type =#{paramCondition.langType}


    and news_time >= #{paramCondition.newsTime}


    and (news_time between #{paramCondition.beginTime} and #{paramCondition.endTime})


    and is_sensitive = #{paramCondition.isSensitive}


    and sensitive_category = #{paramCondition.sensitiveCategory}


    and websitename like CONCAT('%',#{paramCondition.websitename},'%')


    and news_id in

        #{id}


GROUP BY
websitename
ORDER BY num desc

SELECT t1.is_sensitive,COUNT(*) AS num FROM news AS t1 JOIN (SELECT ROUND(0.9*(SELECT MAX(news_id) FROM `news`)) AS news_id) AS t2 WHERE 1=1 AND t1.news_id >= t2.news_id
and lang_type = #{paramCondition.langType}
and is_sensitive = #{paramCondition.isSensitive}
and sensitive_category = #{paramCondition.sensitiveCategory}
and sensitive_words like CONCAT('%',#{paramCondition.sensitiveWords},'%')
and key_words like CONCAT('%',#{paramCondition.keyWords},'%')
and websitename like CONCAT('%',#{paramCondition.websitename},'%')
and (t1.news_time between CONCAT(#{paramCondition.beginTime},' 00:00:00') and CONCAT(#{paramCondition.endTime},' 23:59:59'))
GROUP BY t1.is_sensitive ORDER BY num desc

SELECT
is_sensitive,COUNT(*) AS num FROM news
WHERE 1=1

    and lang_type =#{paramCondition.langType}


    and news_time >= #{paramCondition.newsTime}


    and (news_time between #{paramCondition.beginTime} and #{paramCondition.endTime})


    and is_sensitive = #{paramCondition.isSensitive}


    and sensitive_category = #{paramCondition.sensitiveCategory}


    and websitename like CONCAT('%',#{paramCondition.websitename},'%')


    and news_id in

        #{id}


GROUP BY
is_sensitive
ORDER BY num desc

SELECT t1.sensitive_category,COUNT(*) AS num FROM news AS t1 JOIN (SELECT ROUND(0.9*(SELECT MAX(news_id) FROM `news`)) AS news_id) AS t2 WHERE 1=1 AND t1.news_id >= t2.news_id
and t1.lang_type = #{paramCondition.langType}
and t1.is_sensitive = #{paramCondition.isSensitive}
and sensitive_category = #{paramCondition.sensitiveCategory}
and websitename like CONCAT('%',#{paramCondition.websitename},'%')
and (news_time between CONCAT(#{paramCondition.beginTime},' 00:00:00') and CONCAT(#{paramCondition.endTime},' 23:59:59'))
GROUP BY t1.sensitive_category ORDER BY t1.sensitive_category

SELECT
sensitive_category,COUNT(*) AS num FROM news
WHERE 1=1

    and lang_type =#{paramCondition.langType}


    and news_time >= #{paramCondition.newsTime}


    and (news_time between #{paramCondition.beginTime} and #{paramCondition.endTime})


    and is_sensitive = #{paramCondition.isSensitive}


    and sensitive_category = #{paramCondition.sensitiveCategory}


    and websitename like CONCAT('%',#{paramCondition.websitename},'%')


    and news_id in

        #{id}


GROUP BY
sensitive_category
ORDER BY num desc

SELECT YEAR(news_time) as yearTime, MONTH( news_time) as monthTime, CONCAT(YEAR(news_time),'-',MONTH(news_time)) AS data_time , COUNT(1) AS total, COUNT(IF(is_sensitive=1,1,NULL)) AS neuNum, COUNT(IF(is_sensitive=2,2,NULL)) AS senNum, COUNT(IF(is_sensitive=3,3,NULL)) AS forNum FROM news AS t1 JOIN (SELECT ROUND(0.9*(SELECT MAX(news_id) FROM `news`)) AS news_id) AS t2 where 1=1 and t1.news_id >= t2.news_id and t1.is_sensitive is not null
and lang_type = #{paramCondition.langType}
and websitename like CONCAT('%',#{paramCondition.websitename},'%')
and is_sensitive = #{paramCondition.isSensitive}
and sensitive_category = #{paramCondition.sensitiveCategory}
and sensitive_words like CONCAT('%',#{paramCondition.sensitiveWords},'%')
and key_words like CONCAT('%',#{paramCondition.keyWords},'%')
and (t1.news_time between CONCAT(#{paramCondition.beginTime},' 00:00:00') and CONCAT(#{paramCondition.endTime},' 23:59:59'))
GROUP BY data_time ORDER BY yearTime DESC,monthTime DESC
